import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

function App() {
  const [username, setUsername] = useState('');
  const [isJoined, setIsJoined] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const joinChat = async () => {
    if (username.trim()) {
      // Create or get user
      try {
        await axios.post('/users', null, { params: { username: username.trim() } });
        setIsJoined(true);
        connectWebSocket();
      } catch (error) {
        console.error('Error joining chat:', error);
      }
    }
  };

  const connectWebSocket = () => {
    const ws = new WebSocket(`ws://localhost:8000/ws/${username}`);
    
    ws.onopen = () => {
      console.log('Connected to chat');
      setIsConnected(true);
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      
      switch (data.type) {
        case 'history':
          setMessages(data.messages);
          break;
        case 'new_message':
          setMessages(prev => [...prev, data.message]);
          break;
        case 'user_joined':
          console.log(`${data.username} joined the chat`);
          break;
        case 'user_left':
          console.log(`${data.username} left the chat`);
          break;
        default:
          break;
      }
    };

    ws.onclose = () => {
      console.log('Disconnected from chat');
      setIsConnected(false);
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    setSocket(ws);
  };

  const sendMessage = () => {
    if (inputMessage.trim() && socket && isConnected) {
      socket.send(JSON.stringify({
        type: 'message',
        content: inputMessage.trim()
      }));
      setInputMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  if (!isJoined) {
    return (
      <div className="frontpage-container">
        <div className="join-title">Join Chat</div>
        <input
          type="text"
          className="username-input"
          value={username}
          onChange={e => setUsername(e.target.value)}
          placeholder="Enter your username"
        />
        <button
          onClick={joinChat}
          className="join-button"
          disabled={!username}
        >
          Join Chat
        </button>
      </div>
    );
  }

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>Real-time Chat</h2>
        <div>
          <span>Connected as: <strong>{username}</strong></span>
          {!isConnected && <span> (Reconnecting...)</span>}
        </div>
      </div>
      
      <div className="chat-messages">
        {messages.map((message, index) => (
          <div
            key={message.id || index}
            className={`message ${message.username === username ? 'own' : 'other'}`}
          >
            <div className="message-header">
              <strong>{message.username}</strong> â€¢ {new Date(message.timestamp).toLocaleTimeString()}
            </div>
            <div className="message-content">{message.content}</div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-container">
        <input
          type="text"
          placeholder="Type a message..."
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          className="chat-input"
          disabled={!isConnected}
        />
        <button
          onClick={sendMessage}
          className="send-button"
          disabled={!isConnected || !inputMessage.trim()}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
